function paintGame(state) {

  // draw avatars
  const layer2 = document.getElementById('layer2');
  const ctx2 = layer2.getContext('2d');
  ctx2.clearRect(0, 0, CANVAS_WIDTH, CANVAS_HEIGHT);
  for (let i = 0; i < state.numActivePlayers; i++) {
    ctx2.strokeStyle = state.activePlayers[i].team;
    if (state.activePlayers[i].clientid ===  myClientID) {
      // myx = state.activePlayers[i].posx;
      // myy = state.activePlayers[i].posy;
      ctx2.lineWidth = 2;
    } else {
      ctx2.lineWidth = 1;
    }
    if (state.activePlayers[i].bounced === true) {
      ctx2.strokeStyle = 'yellow';
      if (state.activePlayers[i].afterimage >= 3) {
        ctx2.lineWidth = state.activePlayers[i].afterimage;
      }
    }
    drawCircle(ctx2, state.activePlayers[i].posx, state.activePlayers[i].posy, AVATAR_RADIUS);
    ctx2.font = "8px Copperplate, Papyrus, fantasy";
    ctx2.textAlign = "center";
    if (state.activePlayers[i].team === "red") {
        ctx2.fillStyle = RED;
    } else {
        ctx2.fillStyle = BLUE;
    }

    ctx2.fillText(state.activePlayers[i].name[0], state.activePlayers[i].posx, state.activePlayers[i].posy + 2);
  }

  if (state.numActivePlayers > 0) {
    // display ball
    const layer3 = document.getElementById('layer3');
    const ctx3 = layer3.getContext('2d');
    ctx3.clearRect(0, 0, CANVAS_WIDTH, CANVAS_HEIGHT);
    var soccerball = new Image();
    // soccerball image is from Wikipedia under the GNU Free Documentation License: https://en.wikipedia.org/wiki/File:Soccer_ball.svg
    soccerball.src = "data:image/png;base64,"
    ctx3.drawImage(soccerball, state.ball.pos.x - BALL_WIDTH/2, state.ball.pos.y - BALL_HEIGHT/2);
  }

  // display goals for animation
  drawGoal(ctx1, 'red');
  drawGoal(ctx1, 'blue');

  // scoreboard
  const top_display1 = document.getElementById("top_display1");
  const top_ctx1 = top_display1.getContext("2d");
  top_ctx1.clearRect(0, 0, 360, 150);
  top_ctx1.fillStyle = 'rgba(255, 255, 255, .3)';
  top_ctx1.textAlign = "center";
  numActivePlayers = state.activePlayers.length;

  // display red team score
  top_ctx1.font = getFont(state.score.red);
  top_ctx1.fillText(state.score.red, 100, 90);
  // top_ctx1.fillText(Math.round(myx), 100, 90);
  top_ctx1.fillStyle = 'rgba(255, 255, 255, .3)';
  top_ctx1.font = "24px Copperplate, Papyrus, fantasy";
  display_text = "Red"
  top_ctx1.fillText(display_text, 100, 120);

  // display blue team score
  bounce_count = state.bounce_count;
  top_ctx1.font = getFont(state.score.blue);
  top_ctx1.fillText(state.score.blue, 260, 90);
  // top_ctx1.fillText(Math.round(myy), 260, 90);
  top_ctx1.fillStyle = 'rgba(255, 255, 255, .3)';
  top_ctx1.font = "24px Copperplate, Papyrus, fantasy";
  display_text = "Blue"
  top_ctx1.fillText(display_text, 260, 120);

}

function handleInitClient(clientid) {
  myClientID = clientid;
  console.log("myClientID: " + myClientID)
  init();
}

function handleGameState(gameState) {

  // parse the game state
  gameState = JSON.parse(gameState);

  // send out joystick values (emit the joystick each time the client sends a frame)
  emitJoyStick(joy);

  // paint the game
  requestAnimationFrame(() => paintGame(gameState));
}


function handleUnknownCode() {
  reset();
  alert('Unknown Game Code')
}

function reset() {
  playerNumber = null;
  gameCodeInput.value = '';
  initialScreen.style.display = "block";
  gameScreen.style.display = "none";
}

function drawCircle(ctx, x, y, r) {
  ctx.beginPath();
  ctx.arc(x, y, r, 0, 2 * Math.PI);
  ctx.stroke();
}

function getFont(value) {
  if (value === 0) {
    fontsize = 100;
  } else {
    ndigits = Math.ceil(Math.log10(value + 1));
    fontsize = 100 - 20 * (ndigits-1);
    // console.log("Value: " + value + ", ndigits: " + ndigits + ", fontsize: " + fontsize)
  }
  return fontsize + "px Copperplate, Papyrus, fantasy";
}

function drawGoal(ctx, team) {
    if(team === 'red') {
        ctx.fillStyle = RED;
        ctx.fillRect(2, CANVAS_HEIGHT / 2 - GOAL_RADIUS, 10, 2 * GOAL_RADIUS);
    }
    if(team === 'blue') {
        ctx.fillStyle = BLUE;
        ctx.fillRect(CANVAS_WIDTH - 12, CANVAS_HEIGHT / 2 - GOAL_RADIUS, 10, 2 * GOAL_RADIUS);
    }
}

function isLetter(str) {
  return str.length === 1 && str.match(/[a-z]/i);
}
